; /* Start:"a:4:{s:4:"full";s:54:"/local/templates/stilkuhni/js/script.js?15766623466666";s:6:"source";s:39:"/local/templates/stilkuhni/js/script.js";s:3:"min";s:0:"";s:3:"map";s:0:"";}"*/
$(document).ready(function() {



	var bbrowser = 'notIE';


	if(/Trident/.test(navigator.userAgent)) bbrowser = 'IE';
	if(/Edge/.test(navigator.userAgent)) bbrowser = 'IE';

	if(bbrowser == 'IE'){
        $('#slideshow').css('display', 'none');
        $('#main-slider').show();
    } else {
        $('#main-slider').hide();
        $('#slideshow').css('display', 'block');
    }


	 $('.back_call > a').click( function(event){
        $('#back_call').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });

    $('.slider').slick({
        slidesToShow: 3,
    });
    $('.slider-action').slick({
        slidesToShow: 4,
    });

    $('.slider-dialog').slick({
        slidesToShow: 1,
        dots: true,
        fade: true,
        speed: 1000,
    });
    $('#main-slider').slick({
        slidesToShow: 1,
    });
    $('.slideshow .slick-prev').click( function(event){
		checkBanner('prev');
        return false;
    });
	$('.slideshow .slick-next').click( function(event){
		checkBanner('next');
        return false;
    });

	function checkBanner(type){
		var i = 0;
		var cur = 0;
        $(".nav__item").each(function() {
			if($(this).hasClass('nav__item--current')) cur = i;
			i++;
		});
		if(type=='prev'){
			if(cur==0) cur = i-1; else cur--;
		}
		else {
			if(cur<i-1)cur++; else cur = 0;
		}
		$el = $(".nav__item");
		$el[cur].click();
	}
    $('.slider-for').slick({
        lazyLoad: 'ondemand',
        slidesToShow: 1,
        slidesToScroll: 1,
        arrows: false,
        fade: true,
        asNavFor: '.slider-nav'
    });
    $('.slider-nav').slick({
        slidesToShow: 6,
        slidesToScroll: 1,
        asNavFor: '.slider-for',
        focusOnSelect: true
    });


	$('.price_window').click( function(event){
		var vid = $(this).attr('vid');
		$('.price_window_body').hide();
		$('#price_window'+vid).show();
        $('#price_window').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });
    $('.header_top .search > a').click( function(event){
        $('#search').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });
    /*$('.free_services > a').click( function(event){
        $('#free_services').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });*/
    $('.comment > a').click( function(event){
        $('#recall').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });
    $('.header_top .helper > a').click( function(event){
        $('#helper').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });
    $('.online_inquiry > a').click( function(event){
        $('#online_inquiry').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });
	$('.calculat_kitchen > a').click( function(event){
		$('#type_all').val($('#calculat_kitchen .tabs_caption .active div').attr('vid')).trigger('change');
        $('#calculat_kitchen').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });

    $('.js-entry_salon > a, .btn_record').click( function(event){
        $('#type_all').val($('#entry_salon .tabs_caption .active div').attr('vid')).trigger('change');
        $('#entry_salon').addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });

	$('#calculat_kitchen .tabs_caption').click( function(event){
		$('#type_all').val($('#calculat_kitchen .tabs_caption .active div').attr('vid')).trigger('change');
		return false;
    });

	$('#modal_close_call, .dialog__overlay').click( function(){
        $('.dialog.dialog--open').removeClass('dialog--open').addClass('dialog--close');
        $('body').removeClass('modal-open');
        setTimeout(function () {
            $('.dialog').removeClass('dialog--close')
        }, 1000);
    });

    /*$('.liked').click(function () {
        $(this).toggleClass('active');
        $(this).find('span').toggleClass('hidden');
        return false;
    });*/

    $('.js-image-modal').click( function(event){
        console.log($(this).attr("href"))
        $($(this).attr("href")).addClass('dialog--open');
        $('body').addClass('modal-open');
        return false;
    });

    $(".js-dialog-slider").slick()

	$('.dropdown').on('click', function(event){
		event.preventDefault();
        $(this).parent('li').siblings().removeClass('active').find('.submenu.active').removeClass('active');
        $(this).parent('li').siblings().find('.dropdown.active').removeClass('active');
		$(this).next('.submenu').toggleClass('active');
		$(this).toggleClass('active');
        $(this).parent('li').toggleClass('active');
	});

    $('header, header a, .gray_bg, main').click(function () {
        $('.dropdown').removeClass('active').parent('li').siblings().removeClass('active').find('.submenu.active').removeClass('active');
    });

    $(function() {
        $('ul.tabs_caption').on('click', 'li:not(.active)', function() {
            $(this).addClass('active').siblings().removeClass('active')	.closest('div.tabs').find('div.tabs_content').removeClass('active').eq($(this).index()).addClass('active');
        });
    });
    $(".chosen-select").chosen({
        disable_search_threshold: 20
    });

        $( ".spinner" ).spinner({
      min: 1,
      step: 1,
      start: 1,
      numberFormat: "C"
    });
    $( ".spinner#date" ).spinner({
      max: 31


    });

    $( ".spinner#year" ).spinner({
      min: 2017
    });
	$( ".spinner#year2" ).spinner({
      min: 2017
    });

    $('.accordion-header').on('click', function (e) {
        var $this = $(this);
        $this.parent('.accordion').toggleClass('active');
        $this.next('.accordion-collapse').toggle();
        $this.parent('.accordion').siblings('.accordion').removeClass('active').find('.accordion-collapse').hide();
        e.stopPropagation();
    });
});
$(document).ready(function() {
    $(document).on('submit','#entry_salon_form',function(){
        setTimeout(function(){
            console.log('123');
            if('undefined'!== typeof yaCounter26103399)
                    {
                        yaCounter26103399.reachGoal('salonappoint');
                        return true;
                    }
            });
        },2000);
    $('img').lazyload();
 });

