function fulltime (date,product) {
var time=new Date();
var newYear=new Date(date);

var totalRemains=(newYear.getTime()-time.getTime());

if (totalRemains>1){

var RemainsSec = (parseInt(totalRemains/1000));
var RemainsFullDays=(parseInt(RemainsSec));
var secInLastDay=RemainsSec-RemainsFullDays*24*3600;
var RemainsFullHours=(parseInt(secInLastDay/3600));
if (RemainsFullHours<10){RemainsFullHours="0"+RemainsFullHours};
var secInLastHour=secInLastDay-RemainsFullHours*3600;
var RemainsMinutes=(parseInt(secInLastHour/60));
if (RemainsMinutes<10){RemainsMinutes="0"+RemainsMinutes};
var lastSec=secInLastHour-RemainsMinutes*60;
if (lastSec<10){lastSec="0"+lastSec};

var d = Math.floor(RemainsFullDays/86400);
var last = RemainsFullDays - d*86400;


var h = Math.floor(last/3600);
var last = last - h*3600;
var m = Math.floor(last/60);
var s = Math.floor(last - m*60);


d_title = getNumEnding(d,['', '', '']);

$('.RemainsFullDays'+product).html(d+" "+d_title+"<br> "+timeFormat(h)+" : "+timeFormat(m)+" : "+timeFormat(s));
setTimeout(function() { fulltime(date,product) }, 10)

}
}
